import { useState, useEffect, useLayoutEffect, useMemo } from "react";
import { useLocation } from "react-router-dom";

export default function useKeepPositionScroll() {
  let location = useLocation();
  const [scrollTop, setScrollTop] = useState(0);
  const scrollPosition = useMemo(() => {
    return JSON.parse(sessionStorage.getItem("scroll-position")) || [];
  }, []);

  const windowScrollTop = () => {
    const position = window.pageYOffset;
    setScrollTop(position);
  };

  useLayoutEffect(() => {
    // if (!scrollPosition.length) return;
    const positionByPathname =
      scrollPosition.find((v) => v.pathname === location.pathname)?.position ||
      0;

    console.log(positionByPathname);

    window.scrollTo(0, positionByPathname);
  }, [location.pathname, scrollPosition]);

  useEffect(() => {
    window.addEventListener("scroll", windowScrollTop);
    console.log(1)

    return () => window.removeEventListener("scroll", windowScrollTop);
  }, []);

  useEffect(() => {
    let newScrollPosition = [];

    const id = scrollPosition.findIndex(
      (v) => v.pathname === location.pathname
    );

    if (id !== -1) {
      newScrollPosition = [...scrollPosition];
      newScrollPosition.splice(id, 1, {
        pathname: location.pathname,
        position: scrollTop,
      });

      sessionStorage.setItem(
        "scroll-position",
        JSON.stringify(newScrollPosition)
      );

      return;
    }

    newScrollPosition = [
      ...scrollPosition,
      {
        pathname: location.pathname,
        position: scrollTop,
      },
    ];

    sessionStorage.setItem(
      "scroll-position",
      JSON.stringify(newScrollPosition)
    );
  }, [location.pathname, scrollTop, scrollPosition]);

  return location;
}
